mapper.insert:
insert into mn_point_plus (
	plus_point, id, plus_date, plus_type, 
	merchant_uid, imp_uid
) values (
	#{plusPoint}, #{id}, #{plusDate}, #{plusType},
	#{merchantUid}, #{impUid}
)

insert into mn_point_plus (
	plus_point, id, plus_date, plus_type
) values (
	#{plusPoint}, #{id}, #{plusDate}, #{plusType}
)

insert into mn_point_minus (
	minus_point, id, minus_date, minus_use,
	fees
) values (
	#{minusPoint}, #{id}, #{minusDate}, #{minusUse},
	#{fees}
)

mapper.update:
update mn_member
   set point = #{point}
  where id = #{id}

mapper.select:
select count(*)
 from mn_point_plus
   where id = #{id}

select count(*)
from mn_point_minus
where id = #{id}
